import React, { Fragment, useState } from 'react';
import ReactDOM from 'react-dom';
import BodyProducts from './Products';

const BodyCategories = ({setTotalPrice}) => {

    const [opened, setOpened] = useState(null)

    const categorias = [
        {
            id: 1,
            nome:'Pizza' , 
            produtos:[
                {id: 1, nome:'Broto', price:30},
                {id: 2, nome:'Media', price:40},
                {id: 3, nome:'Grande', price:50},
        ]},
        {
            id: 2,
            nome:'Drinks' , 
            produtos:[
                {id: 1, nome:'Coca-Cola', price:4},
                {id: 2, nome:'Sprite', price:5},
                {id: 3, nome:'Fanta Uva', price:5},
                {id: 4, nome: 'Agua', price: 1},
        ]},
        {
            id: 3,
            nome:'Massas' , 
            produtos:[
                {id: 1, nome:'Carbonara', price:15},
                {id: 2, nome:'Ao Molho', price:20},
                {id: 3, nome:'Molho Gorgonzola', price:25},
                {id: 4, nome: 'Nhoque', price: 20},
        ]},
    ]

    return (
        <div class='MainProducts'>
        {categorias.map((categoria, index) => {
            return (
        <div class='category'>
        <p class='foodTitle'>{categoria.nome}<button class='openList'onClick ={ () => {setOpened(categoria.id === opened ? null : categoria.id)}} margin='5px'>V</button></p>
        {opened === categoria.id && <BodyProducts categoria={categoria} setTotalPrice = {setTotalPrice}/>}
        </div> )}
        )}
        </div>
    )
}

export default BodyCategories;
