import React from "react";
import { ReactDOM } from "react";
import BodyCategories from "./Categories";
import Total from "../footer/Total";

const BodyProducts = ({categoria, setTotalPrice, setCount}) => {


    return (
        <div class='SubProducts'>
            {categoria?.produtos.map((produto, index) => {
            const decreasePrice = () => {
                setTotalPrice((prev_state) => prev_state - produto.price)
            }

            const increasePrice = () => {
                setTotalPrice((prev_state) => prev_state + produto.price)
            }

            const exist = categoria.produtos.find((x) => x.id === produto.id);
                if (exist) {
                    setCount(
                        categoria.produtos.map((x) => x.id === produto.id ? {...exist, qty: exist.qty + 1} : x)
                    )
                } else {
                    setCount([...categoria.produtos, {...produto, qty: 0}]);
                }
               return (
                   <div class='subProduto'>
                   <p class='subNome'>{produto.nome}</p>
                   <button class='adtake' onClick={decreasePrice}>
                    -
                   </button>
                   <button class='adtake' onClick={increasePrice}>
                    +
                   </button>
                   </div>
               )
                })}
        </div>
    )
}

export default BodyProducts;
