import React from "react";
import { ReactDOM } from "react";
import BodyCategories from "./Categories";
import Total from "../footer/Total";

const BodyProducts = ({ App, categoria, setTotalPrice, setCount, count }) => {
  return (
    <div class="SubProducts">
      {categoria?.produtos.map((produto, index) => {
        const decreasePrice = () => {
          setTotalPrice((prev_state) => prev_state - produto.price);
        };

        const increasePrice = () => {
          setTotalPrice((prev_state) => prev_state + produto.price);
        };

        const increaseQty = () => {
          setCount((prev) => ({
            ...prev,
            [produto.id]: prev[produto.id] + 1,
          }));
        };
        const decreaseQty = () => {
          /*console.log(count);
          console.log({ ...count, 1: "Bartho" }, "spread operator");
          console.log(produto.id);
          console.log(categoria.id);*/
          // ...(spread operator) espalha o elemento (nesse caso, count recebe 1:0, 2:0, 3:0, 4:0), e envia um novo valor para uma dessas propriedades
          console.log(categoria.id, produto.id);
          console.log();
          {
            setCount((prev) => ({
              ...prev,
              [produto.id]: prev[produto.id] - 1,
            }));
          }
        };

        const decrease = () => {
          decreasePrice();
          decreaseQty();
        };

        const increase = () => {
          increasePrice();
          increaseQty();
        };

        if (count[produto.id] <= 0) {
          count[produto.id] = 0;
        }

        return (
          <div class="subProduto">
            <p class="subNome">{produto.nome}</p>
            <div class="adder">
              <button
                class="adtake"
                onClick={decrease}
                disabled={count[produto.id] === 0}
              >
                -
              </button>
              <div>{count[produto.id]}</div>
              <button class="adtake" onClick={increase}>
                +
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default BodyProducts;
