import React, { Fragment, useState } from 'react';
import ReactDOM from 'react-dom';
import BodyProducts from './Products';

const BodyCategories = () => {

    const [opened, setOpened] = useState(null)

    const categorias = [
        {
            id: 1,
            nome:'Pizza' , 
            produtos:[
                {id: 1, nome:'Broto'},
                {id: 2, nome:'Media'},
                {id: 3, nome:'Grande'},
        ]},
        {
            id: 2,
            nome:'Drinks' , 
            produtos:[
                {id: 1, nome:'Coca-Cola'},
                {id: 2, nome:'Sprite'},
                {id: 3, nome:'Fanta Uva'},
        ]},
    ]

    return (
        <div class='MainProducts'>
        {categorias.map((categoria, index) => {
            return (
        <div onClick ={ () => {setOpened(categoria.id === opened ? null : categoria.id)}} class='category'>
        <p class='foodTitle'>{categoria.nome}</p>
        {opened === categoria.id && <BodyProducts categoria={categoria}/>}
        </div> )}
        )}
        </div>
    )
}

export default BodyCategories;
