import React from "react";

const BodyProducts = ({ categoria, setTotalPrice, setCount, count }) => {
  return (
    <div class="SubProducts">
      {categoria?.produtos.map((produto, index) => {
        var result = count.map((prod) => ({
          prod_id: prod.prod_id,
          cat_id: categoria.id,
          qtd: prod.qtd,
        }));

        const decreasePrice = () => {
          setTotalPrice((prev_state) => prev_state - produto.price);
        };

        const increasePrice = () => {
          setTotalPrice((prev_state) => prev_state + produto.price);
        };

        const increaseQty = () => {
          setCount((prev_count) => prev_count(result[index].qtd + 1));
        };

        const decreaseQty = () => {};

        const decrease = () => {
          decreasePrice();
          decreaseQty();
        };

        const increase = () => {
          increasePrice();
          increaseQty();
          console.log(
            produto.id,
            categoria.id,
            produto.nome,
            result[index].prod_id,
            result[index].cat_id,
            result[index].qtd
          );
        };

        return (
          <div class="subProduto">
            <p class="subNome">{produto.nome}</p>
            <div class="adder">
              <button
                class="adtake"
                onClick={() => decrease(produto.id, categoria.id)}
                disabled={count[produto.id].qtd === 0}
              >
                -
              </button>
              <div>{count[produto.id].qtd}</div>
              <button class="adtake" onClick={increase}>
                +
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default BodyProducts;
