import React, { Fragment, useState } from 'react';
import ReactDOM from 'react-dom';
import BodyProducts from './Products';

const BodyCategories = () => {

    const [opened, setOpened] = useState(null)

    const categorias = [
        {
            id: 1,
            nome:'Pizza' , 
            produtos:[
                {id: 1, nome:'Broto', price:'30'},
                {id: 2, nome:'Media', price:'40'},
                {id: 3, nome:'Grande', price:'50'},
        ]},
        {
            id: 2,
            nome:'Drinks' , 
            produtos:[
                {id: 1, nome:'Coca-Cola', price:'4'},
                {id: 2, nome:'Sprite', price:'5'},
                {id: 3, nome:'Fanta Uva', price:'5'},
        ]},
    ]

    return (
        <div class='MainProducts'>
        {categorias.map((categoria, index) => {
            return (
        <div class='category'>
        <p class='foodTitle'>{categoria.nome}<button onClick ={ () => {setOpened(categoria.id === opened ? null : categoria.id)}} /></p>
        {opened === categoria.id && <BodyProducts categoria={categoria}/>}
        </div> )}
        )}
        </div>
    )
}

export default BodyCategories;
