import React from "react";

const BodyProducts = ({ categoria, setTotalPrice, setCount, count }) => {
  return (
    <div class="SubProducts">
      {categoria?.produtos.map((produto, index) => {
        var result = count.map((prodId) => ({
          prod_id: prodId.prod_id,
          cat_id: prodId.cat_id,
          qtd: prodId.qtd,
        }));

        var productId = result.map((produtoID) => {
          return produtoID.prod_id;
        });

        var categoryId = result.map((categoriaID) => {
          return categoriaID.cat_id;
        });

        var quantity = result.map((quantidade) => {
          return quantidade.qtd;
        });

        const decreasePrice = () => {
          setTotalPrice((prev_state) => prev_state - produto.price);
        };

        const increasePrice = () => {
          setTotalPrice((prev_state) => prev_state + produto.price);
        };

        const increaseQty = (element, index, array) => {
          console.log(productId[index], produto.id);
          if (
            productId[index] === produto.id &&
            categoryId[index] === categoria.id
          ) {
            setCount(result.qtd + 1);
            console.log(result.qtd);
          }
        };
        const decreaseQty = (element, index, array) => {};

        const decrease = (prod_id, cat_id) => {
          decreasePrice();
          decreaseQty(prod_id, cat_id);
        };

        const increase = (prod_id, cat_id) => {
          increasePrice();
          increaseQty(prod_id, cat_id);
        };

        return (
          <div class="subProduto">
            <p class="subNome">{produto.nome}</p>
            <div class="adder">
              <button
                class="adtake"
                onClick={() => decrease(produto.id, categoria.id)}
                disabled={count[produto.id].qtd === 0}
              >
                -
              </button>
              <div>{count[produto.id].qtd}</div>
              <button class="adtake" onClick={increase}>
                +
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default BodyProducts;
